import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [searchQuery, setSearchQuery] = useState('');
  const [results, setResults] = useState({ stocks: [], funds: [] });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [selectedItem, setSelectedItem] = useState(null);

  const search = async (query) => {
    if (!query) return;

    setLoading(true);
    setError(null);

    try {
      const response = await fetch(`http://localhost:3001/api/search?query=${encodeURIComponent(query)}`);
      const data = await response.json();
      setResults(data);
    } catch (err) {
      setError('Error searching data. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const fetchDetails = async (type, isin) => {
    setLoading(true);
    setError(null);

    try {
      const response = await fetch(`http://localhost:3001/api/${type}/${isin}`);
      const data = await response.json();
      setSelectedItem(data);
    } catch (err) {
      setError('Error fetching details. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="app">
      <header className="header">
        <h1>NewsSense</h1>
        <p>Indian Market Data Search</p>
      </header>

      <main className="main">
        <div className="search-container">
          <input
            type="text"
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            placeholder="Search by ISIN, ticker, or name..."
            className="search-input"
          />
          <button
            onClick={() => search(searchQuery)}
            className="search-button"
            disabled={loading}
          >
            {loading ? 'Searching...' : 'Search'}
          </button>
        </div>

        {error && <div className="error">{error}</div>}

        {selectedItem ? (
          <div className="details">
            <h2>{selectedItem.name}</h2>
            <div className="details-grid">
              <div className="detail-item">
                <span className="label">ISIN:</span>
                <span className="value">{selectedItem.isin}</span>
              </div>
              {selectedItem.ticker && (
                <div className="detail-item">
                  <span className="label">Ticker:</span>
                  <span className="value">{selectedItem.ticker}</span>
                </div>
              )}
              <div className="detail-item">
                <span className="label">{selectedItem.nav ? 'NAV' : 'Price'}:</span>
                <span className="value">{selectedItem.nav || selectedItem.lastPrice}</span>
              </div>
              <div className="detail-item">
                <span className="label">Change:</span>
                <span className={`value ${selectedItem.change >= 0 ? 'positive' : 'negative'}`}>
                  {selectedItem.change} ({selectedItem.changePercent}%)
                </span>
              </div>
              {selectedItem.volume && (
                <div className="detail-item">
                  <span className="label">Volume:</span>
                  <span className="value">{selectedItem.volume.toLocaleString()}</span>
                </div>
              )}
            </div>
            <button onClick={() => setSelectedItem(null)} className="back-button">
              Back to Search
            </button>
          </div>
        ) : (
          <div className="results">
            {results.stocks.length > 0 && (
              <div className="result-section">
                <h2>Stocks</h2>
                {results.stocks.map((stock) => (
                  <div
                    key={stock.isin}
                    className="result-item"
                    onClick={() => fetchDetails('stock', stock.isin)}
                  >
                    <div className="result-header">
                      <span className="name">{stock.name}</span>
                      <span className={`price ${stock.change >= 0 ? 'positive' : 'negative'}`}>
                        {stock.lastPrice}
                      </span>
                    </div>
                    <div className="result-details">
                      <span className="ticker">{stock.ticker}</span>
                      <span className="change">
                        {stock.change} ({stock.changePercent}%)
                      </span>
                    </div>
                  </div>
                ))}
              </div>
            )}

            {results.funds.length > 0 && (
              <div className="result-section">
                <h2>Mutual Funds</h2>
                {results.funds.map((fund) => (
                  <div
                    key={fund.isin}
                    className="result-item"
                    onClick={() => fetchDetails('fund', fund.isin)}
                  >
                    <div className="result-header">
                      <span className="name">{fund.name}</span>
                      <span className={`price ${fund.change >= 0 ? 'positive' : 'negative'}`}>
                        {fund.nav}
                      </span>
                    </div>
                    <div className="result-details">
                      <span className="change">
                        {fund.change} ({fund.changePercent}%)
                      </span>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
