import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [serverStatus, setServerStatus] = useState('checking');
  const [stocks, setStocks] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedStock, setSelectedStock] = useState(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    checkServerStatus();
    fetchStocks();
  }, []);

  const checkServerStatus = async () => {
    try {
      const response = await axios.get('http://localhost:3001/api/health');
      setServerStatus(response.data.status);
    } catch (error) {
      setServerStatus('error');
    }
  };

  const fetchStocks = async () => {
    try {
      const response = await axios.get('http://localhost:3001/api/stocks');
      setStocks(response.data);
    } catch (error) {
      console.error('Error fetching stocks:', error);
    }
  };

  const handleSearch = async (query) => {
    setLoading(true);
    try {
      const response = await axios.get(`http://localhost:3001/api/stocks/search?q=${query}`);
      setStocks(response.data);
    } catch (error) {
      console.error('Error searching stocks:', error);
    }
    setLoading(false);
  };

  const handleStockSelect = (stock) => {
    setSelectedStock(stock);
  };

  const formatPrice = (price) => {
    return new Intl.NumberFormat('en-IN', {
      style: 'currency',
      currency: 'INR'
    }).format(price);
  };

  const formatChange = (change) => {
    const color = change >= 0 ? '#4CAF50' : '#F44336';
    return (
      <span style={{ color }}>
        {change >= 0 ? '+' : ''}{change.toFixed(2)}%
      </span>
    );
  };

  return (
    <div style={{ padding: '20px', maxWidth: '1200px', margin: '0 auto', fontFamily: 'Arial, sans-serif' }}>
      <h1 style={{ color: '#333', textAlign: 'center', marginBottom: '20px' }}>
        Stock Market Dashboard
      </h1>

      <div style={{ marginBottom: '20px' }}>
        <input
          type="text"
          placeholder="Search stocks..."
          value={searchQuery}
          onChange={(e) => {
            setSearchQuery(e.target.value);
            handleSearch(e.target.value);
          }}
          style={{
            width: '100%',
            padding: '10px',
            fontSize: '16px',
            borderRadius: '4px',
            border: '1px solid #ddd'
          }}
        />
      </div>

      {loading ? (
        <div style={{ textAlign: 'center', padding: '20px' }}>Loading...</div>
      ) : (
        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(300px, 1fr))', gap: '20px' }}>
          {stocks.map((stock) => (
            <div
              key={stock.symbol}
              onClick={() => handleStockSelect(stock)}
              style={{
                padding: '20px',
                border: '1px solid #ddd',
                borderRadius: '4px',
                cursor: 'pointer',
                backgroundColor: selectedStock?.symbol === stock.symbol ? '#f5f5f5' : 'white'
              }}
            >
              <h3>{stock.symbol}</h3>
              <p>Price: {formatPrice(stock.price)}</p>
              <p>Change: {formatChange(stock.change)}</p>
              <p>Volume: {stock.volume.toLocaleString()}</p>
            </div>
          ))}
        </div>
      )}

      {selectedStock && (
        <div style={{ marginTop: '20px', padding: '20px', border: '1px solid #ddd', borderRadius: '4px' }}>
          <h2>{selectedStock.symbol} - Historical Data</h2>
          <div style={{ height: '300px', marginTop: '20px' }}>
            {/* We'll add the chart component here */}
            <p>Chart visualization coming soon...</p>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
